function getColSum(matrix, column) {
  let sum = 0;
  for (let i = 0; i < matrix.rows; i++) {
    sum += matrix[i][column];
  }
  return sum;
}

function maxSumColIndex(data) {
  let maxIndex = 0;
  let maxSum = -Infinity;
  for (let i = 0; i < data.columns; ++i) {
    let currentSum = getColSum(data, i);
    if (currentSum > maxSum) {
      maxSum = currentSum;
      maxIndex = i;
    }
  }
  return maxIndex;
}


